<?if (!defined('FROM_INDEX')) go_to_error404_page();if ($_POST['article-reg-button']){	if ($_POST['title'])	{		$id = add_new_article($_POST['title'], $_POST['writer_id']);			$mode = 'by_id';	}}else if ($_POST['delete-article-button']){	delete_article($_POST['id']);}else if ($_POST['update-article-button']){	if ($_FILES['img']['size'] != 0 and $_FILES['img']['size'] <= 10240000 and 		($_FILES['img']['type'] == 'image/jpeg' or $_FILES['img']['type'] == 'image/bmp'))	{		$img_name = get_current_time_int().'.jpg';		$full_path = FULL_ARTICLES_GALLERY_PATH . $img_name;			if (move_uploaded_file($_FILES['img']['tmp_name'], $full_path))		{ // Здесь идет процесс загрузки изображения			$size = getimagesize($full_path); // с помощью этой функции мы можем получить размер пикселей изображения						if ($size[0] > 5000 or $size[1] > 5000) unlink($full_path);			else set_article_img($_POST['id'], $img_name);		}	}		update_article($_POST['id'], $_POST['title'], $_POST['text'], date_to_int($_POST['date']),        $_POST['popularity'], $_POST['on_main'], $_POST['views_count'], $_POST['description'], $_POST['keywords']);}else if ($_POST['comment-reg-button']){	add_new_comment_for_admin($_POST['author_info'], $_POST['article_id'], 0, $_POST['text'], date_to_int($_POST['date']));}else if ($_POST['delete-comment-button']){	delete_comment($_POST['id']);}else if ($_POST['update-writer-button']){    update_writer($_POST['id'], $_POST['name'], $_POST['link']);    unset($_POST);}// -----$sliders[0] = get_slider_data(1);$sliders[1] = get_slider_data(2);	for ($j = 0; $j < count($sliders); $j++){	if ($j == 0) $slider_comment = 'слайдер на главной';	else $slider_comment = 'слайдер в разделе "товары"';		echo '<h2>Редактирование слайдера №'.($j+1).' ('.$slider_comment.'):</h2>';	$slider_images = $sliders[$j];		for ($i = 0; $i < count($slider_images); $i++)	{		if(count($slider_images) > 2)		{			$deleting_form = get_delete_smth_form($slider_images[$i]['id'], 'delete-img-button');			$deleting_form['button'] = str_replace('class="mini_button"', 'class="normal_button"', $deleting_form['button']);			$deleting_form['button'] = str_replace('Удалить', 'Удалить изображение', $deleting_form['button']);		}				$img = '			<div style="width: 685px; height: 685px;">				<img src="'.$slider_images[$i]['img'].'" style="width: 100%; height: 100%;">			</div>			';						echo '<h3>Картинка №'.($i + 1).':</h3>';		?>			<?=$img?><br>			<form>				Изменить: выберите файл для загрузки <span>(итоговый размер: 685x685; файл меньше 10МБ, меньше 5000x5000 точек)</span>:<br> 				<input type="file" name="img">				<br><br>            				<input type="hidden" name="id" value="<?=$slider_images[$i]['id']?>"/>							Текст:<br>				<input type="text" name="text" value="<?=$slider_images[$i]['text']?>" maxlength="200" id="text_input"/><br>				<br>				Ссылка:<br>				<input type="text" name="link" value="<?=$slider_images[$i]['link']?>" maxlength="200" id="text_input"/><br>				<br>				Индекс изображения <span>(влияет на очередность изображений в слайдере: изображения показываются по возрастанию индекса)</span>:<br>				<input type="text" name="tab_index" value="<?=$slider_images[$i]['tab_index']?>" maxlength="200" id="mini_text_input"/>				<br><br>				<input type="submit" value="Сохранить изменения" name="update-img-button" id="button" class="normal_button">			</form>			<br>						<?=$deleting_form['button']?>            			<br><br><br>		<?        echo $deleting_form['modal'];		if (isset($slider_images[$i + 1]))			echo '-------------------------------------------------------------------------------------------------';	}		?>		<br>	<h3> Добавить еще одно изображение в этот слайдер? </h3>	<form method="post" action="">		<input type="hidden" name="slider_id" value="<?=$sliders_images[0]['slider_id']?>"/>		<input type="submit" value="Добавить" name="image-reg-button" id="button" class="normal_button">	</form>		<?	if (isset($sliders[$j + 1]))	{		echo '<br><br><br>';		echo '<b>-------------------------------------------------------------------------------------------------</b>';		echo '<br><br><br>';	}}?>