<?function add_new_article($title, $writer_id){	$query = "INSERT INTO articles SET title = '$title', writer_id = '$writer_id'";	mysql_query($query);		return mysql_insert_id();}function delete_article($id){// добавить таблицу типа "удаленные статьи"	$query = "DELETE FROM articles WHERE id = $id";	mysql_query($query);}function get_articles_list_by_writer_id($writer_id){	$query = "SELECT * FROM articles WHERE writer_id = '$writer_id'";	return get_norm_mysql_rows($query);}function update_article($id, $title, $text, $description, $keywords, $date){	$query = "UPDATE articles SET 		title = '$title', 		text = '$text',		description = '$description', 		keywords = '$keywords',		date = '$now'		WHERE id = '$id'	";			mysql_query($query);}function get_articles_list(){	$query = "SELECT * FROM articles";	return get_norm_mysql_rows($query);}function get_article_data($id){	$query = "SELECT * FROM articles WHERE id = '$id'";	$query = mysql_query($query);	return mysql_fetch_assoc($query);}?>